mapper.select:
SELECT e.ID eid , e.EmpName, e.EmpNo, e.Pwd, e.DeptID, e.Remark, e.Disabled, d.ID did , d.DeptName, d.Remark dRemark, d.Disabled dDisabled FROM sy_emp e , sy_dept d
WHERE e.DeptID = d.ID and e.Disabled = 0

    AND  e.EmpNo = #{empNo}


    and e.Pwd = #{pwd} ;

SELECT e.ID eid , e.EmpName, e.EmpNo, e.Pwd, e.DeptID, e.Remark, e.Disabled, d.ID did , d.DeptName, d.Remark dRemark, d.Disabled dDisabled FROM sy_emp e , sy_dept d
WHERE e.DeptID = d.ID and e.Disabled = 0

    AND  e.ID = #{id}
